sqlMap.select:
/* [Award_SqlMap.xml] lightpack.award.dao.award.listByAwardRootId */ WITH CTE AS ( SELECT CONVERT(NVARCHAR, '/' + CONVERT(VARCHAR, SORT_ORDER+1000)) PATH_ORDER, 1 INDENTATION, A1.* FROM IKEP4_AW_AWARD A1 WHERE BOARD_ROOT_ID = #awardRootId# AND PORTAL_Id = #portalId# AND BOARD_DELETE = 0 AND BOARD_PARENT_ID is null AND MOBILE_USE = 1 UNION ALL SELECT CONVERT(NVARCHAR, CTE.PATH_ORDER + '/' + CONVERT(VARCHAR, A2.SORT_ORDER+1000)) PATH_ORDER, INDENTATION + 1, A2.* FROM IKEP4_AW_AWARD A2, CTE WHERE A2.BOARD_ROOT_ID = #awardRootId# AND A2.BOARD_DELETE = 0 AND A2.MOBILE_USE = 1 AND CTE.BOARD_ID = A2.BOARD_PARENT_ID ) SELECT INDENTATION, (SELECT COUNT(*) FROM IKEP4_AW_AWARD B WHERE CTE.BOARD_ID = B.BOARD_PARENT_ID AND B.BOARD_DELETE = 0) AS HAS_CHILDREN,
FROM CTE
ORDER BY PATH_ORDER
