sqlMap.insert:
INSERT INTO ikep4_officesupplies_category (
 BOARD_ID,CATEGORY_ID,CATEGORY_NAME,REGISTER_ID,REGISTER_NAME, REGIST_DATE,DELETE_YN
) VALUES (
 #boardId#,#categoryId#,#addNameList#,#registerId#,#registerName#, CURRENT_TIMESTAMP,'Y'
)

sqlMap.select:
with cte1 as( SELECT team_id, isnull(sum(price2),0) as price2 FROM ikep4_officesupplies AS A where A.status1 = 'A' AND convert(varchar(4), A.regist_date, 112) = #year# and A.status2 = 'A' GROUP BY team_id), cte2 as( select m.team_id as team_id,m.team_name as team_name from ikep4_officesupplies_team_manager m where m.use_yn = 'Y'
and m.team_id = #teamId#
) select cte2.team_id as team_id,cte2.team_name as team_name, isnull(cte1.price2,0) as price2 from cte2 left outer join cte1 on cte2.team_id = cte1.team_id group by cte2.team_id,cte2.team_name, cte1.price2 order by cte2.team_name
