SELECT COUNT(*) FROM ( SELECT A.FILE_ID, A.FILE_PATH, A.FILE_NAME, A.FILE_REAL_NAME, A.FILE_CONTENTS_TYPE, A.FILE_EXTENSION, A.FILE_SIZE, A.EDITOR_ATTACH, A.REGISTER_ID, A.REGISTER_NAME, A.REGIST_DATE, A.UPDATER_ID, A.UPDATER_NAME, A.UPDATE_DATE, '' ITEM_ID, '' ITEM_TYPE_CODE FROM IKEP4_DM_FILE A INNER JOIN IKEP4_DM_FILE_LINK B ON A.FILE_ID = B.FILE_ID WHERE B.ITEM_TYPE_CODE = 'SDC'
AND convert(datetime, substring(A.FILE_REAL_NAME, 1,4)+substring(A.FILE_REAL_NAME, 6,2)+'01') < DATEADD(DD, 1, #endDate#) AND convert(datetime, substring(A.FILE_REAL_NAME, 1,4)+substring(A.FILE_REAL_NAME, 6,2)+'01') >= #startDate#
) tbl1

/* lightpack.planner.dao.Schedule.searchScheduleCount */ SELECT count(*) FROM ( SELECT a.CATEGORY_ID FROM IKEP4_SD_SCHEDULE a LEFT OUTER JOIN IKEP4_SD_PARTICIPANT c on a.SCHEDULE_ID = c.SCHEDULE_ID AND c.TARGET_TYPE = 1 LEFT OUTER JOIN IKEP4_SD_REPEAT b ON a.SCHEDULE_GROUP_ID = b.SCHEDULE_GROUP_ID and CONVERT(DATETIME, CONVERT(VARCHAR, a.START_DATE, 23)) BETWEEN b.REPEAT_START_DATE AND b.REPEAT_END_DATE WHERE 1 = 1
AND ( a.WORKSPACE_ID in (SELECT wm.WORKSPACE_ID FROM IKEP4_WS_MEMBER wm INNER JOIN IKEP4_WS_WORKSPACE ww on wm.WORKSPACE_ID = ww.WORKSPACE_ID WHERE wm.MEMBER_ID = #targetId# AND wm.MEMBER_LEVEL IN ('1', '2', '3', '4')) OR (c.TARGET_USER_ID = #targetId#) )
AND a.WORKSPACE_ID = #targetId#
AND ( (a.wholeday = 0 AND a.START_DATE < DATEADD(DD, 1, #endDate#) AND a.END_DATE > #startDate#) OR (a.wholeday = 1 AND a.START_DATE < DATEADD(DD, 1, #endDate#) AND a.END_DATE >= #startDate#) )
UPPER(a.$searchFields[]$) like ('%' + UPPER(#keyword#) + '%')
) p, IKEP4_SD_CATEGORY q WHERE p.CATEGORY_ID = q.CATEGORY_ID
